import React from "react";

function ToDoItem(props) {

    const [isDone, setIsDone] = React.useState(false);

    function handleClick() {
        setIsDone((prevValue) => {
            return !prevValue;
        })
    }

    return (
        <div >
            <li style={{ textDecoration: isDone ? "line-through" : "none" }}>{props.text}</li>
        </div>

    )

}

export default ToDoItem;
